import React, {Component} from 'react';
import 'isomorphic-fetch';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            show:true,
         }
    }

    static async getInitialProps(){
       // fetching movie poster

        const poster = await fetch(this.props.data.posterurl).then(
            res => res.blob()
            ).then(
            blob => URL.createObjectURL(blob)).catch(error => console.error())
            console.log(poster)

        return poster   
    }



    render() { 
    const poster = getInitialProps()


        return (
            <div>
                <img className='img_container' src={poster} />
                <h3>{}</h3>
            </div>
        )
    }
}

export default Movie;
