import React, {Component} from 'react';
import 'isomorphic-fetch';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            show:true,
            blob:null
         }
    }

    async componentDidMount(){

        const poster = await fetch(this.props.posterurl).then(res => res.blob())
        this.setState({blob:poster})        
    }



    render() { 

        // async function loadImage(url) {
