import React, {Component} from 'react';
import 'isomorphic-fetch';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            show:true,
         }
    }

    static async getInitialProps(){
       // fetching movie poster

        const poster = await fetch(this.props.data.posterurl).then(
            res => res.blob()
            ).then(
            blob => URL.createObjectURL(blob)).catch(error =>{error} )

        return {posterurl:poster}   
    }



    render() { 


        return (
            <div className='movie'>
                <div className="bg_color" >
                    <div className='poster_bg_gradient'></div>
                </div>
                <img className='img_container' alt={this.props.data.originalTitle + ' Poster'} src={this.props.data.posterurl} />
                <p className='name_movie'>{this.props.data.originalTitle}</p>
            </div>
        )
    }
}

export default Movie;
