import React, {Component} from 'react';



class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            show:true
         }
    }

    async componentDidMount(){
        const img = await fetch(this.props.posterurl).then( data => console.log(data) )
    }
    render() { 
        return (
            <div>
                {this.state.show ? <div>{this.props.posterurl}</div> : none}
            </div>
        )
    }
}

export default Movie;
