import React, {Component} from 'react';
import 'isomorphic-fetch';

class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            show:true,
            poster:null,
         }
    }

    async componentDidMount(){
       // fetching movie poster

        const poster = await fetch(this.props.data.posterurl).then(
            res => res.blob()
            ).then(
            blob => URL.createObjectURL(blob).catch(error => console.error())
        )

        this.setState({poster:poster})        
    }



    render() { 

        return (
            <div>
                <img className='img_container' src={this.state.poster} />
                <h3>{}</h3>
            </div>
        )
    }
}

export default Movie;
