import React, {Component} from 'react';



class Movie extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            show:true,
            url:null
         }
    }

    componentDidMount(){
        fetch(this.props.posterurl).then( data => this.setState({url:data.posterurl}) )
    }
    render() { 
        return (
            <div>
                {this.state.show ? <div>{this.state.url}</div> : none}
            </div>
        )
    }
}

export default Movie;
